<script>
	import { page, navigating } from "$app/stores"
	import { cart } from "$lib/stores.js"
	import Cart from "./Cart.svelte"

	export let products

	let dialog, active = false

	function toggle() {
		if (active) {
			document.body.style.overflow = 'auto'
			active = false
		} else {
			window.scrollTo(0, 0);
			document.body.style.overflow = 'hidden'
			active = true
		}
	}

	$: if ($navigating) {
		active = false
		document.body.style.overflow = 'auto'
	}
</script>

